Method.Implementation:
	set pContinue = $$$YES
	/// maybe require only for OPTIONS
	/// https://msdn.microsoft.com/en-us/library/aa142827(v=exchg.65).aspx
	/// https://www.webdavsystem.com/server/documentation/ms_office_read_only/
    do %response.SetHeader("DAV","1, 2")
    q 1

	#dim %response As %CSP.Response
	#dim %request As %CSP.Request
	set depth = 1
	if $isobject($g(%request))  {
		set %response.Status = "207 Multi Status"

		set depth = %request.GetCgiEnv("HTTP_DEPTH", 0)				
	}
	set isCollection = ..isCollection(href)
	s ^dbg($i(^dbg)) = $lb(href, depth, isCollection, $zt($p($h,",",2)),@..#FOLDER)

	set multi = ##class(WebDav.XML.multistatus).%New()
	do multi.response.Insert(..CreateResponse(href))

	if isCollection,depth {		
		#dim rs As %ResultSet = ##class(%File).FileSetFunc(@..#FOLDER)

		while (rs.%Next()) {
			set path = rs.Name		
			set name = rs.ItemName

			do multi.response.Insert(..CreateResponse(href _ "/" _ name))
		}
	}

	set w=##class(%XML.Writer).%New()
	set w.DefaultNamespace = "DAV:"
 	set w.Indent=1

 	return w.RootObject(multi)

//w ..getClassXData(,"LOCK2") q 1
set prop = ##class(WebDav.XML.prop).%New() set prop.lockdiscovery = ##class(WebDav.XML.lockdiscovery).%New()
set active = ##class(WebDav.XML.activelock).%New() set active.locktype = ##class(WebDav.XML.locktype).%New() set active.lockscope = ##class(WebDav.XML.lockscope).%New() set active.lockscope.exclusive = ##class(WebDav.XML.empty).%New() set active.depth = 0 set active.timeout = "Second-3600"
set active.owner = ##class(WebDav.XML.owner).%New() set active.owner.content = "ed-pc\eduard"
set active.lockroot = ##class(WebDav.XML.lockroot).%New() do active.lockroot.href.Insert(..getBase() _ href)
set active.locktoken = ##class(WebDav.XML.locktoken).%New() do active.locktoken.href.Insert("opaquelocktoken:" _ $System.Util.CreateGUID())
//set exclusive = ##class(WebDav.XML.lockentry).%New() //set exclusive.locktype.write = ##class(WebDav.XML.write).%New()
//set shared = ##class(WebDav.XML.lockentry).%New() //set shared.lockscope.shared = ##class(WebDav.XML.empty).%New() //set shared.locktype.write = ##class(WebDav.XML.write).%New()
do prop.lockdiscovery.activelock.Insert(active)
set w=##class(%XML.Writer).%New() set w.DefaultNamespace = "DAV:" set w.Indent=1
return w.RootObject(prop)
